"use client";

import type { ContextType } from "./use-button-group";

import { createContext } from "@/lib/react/context";

export const [ButtonGroupProvider, useButtonGroupContext] = createContext<ContextType>({
  name: "ButtonGroupContext",
  strict: false,
});

import type { UseButtonGroupProps } from "./use-button-group";

import { ButtonGroupProvider } from "./button-group-context";
import { useButtonGroup } from "./use-button-group";

import { forwardRef } from "@/lib/system";

export interface ButtonGroupProps extends UseButtonGroupProps {}

const ButtonGroup = forwardRef<"div", ButtonGroupProps>((props, ref) => {
  const { Component, domRef, context, children, classNames, getButtonGroupProps } = useButtonGroup({
    ...props,
    ref,
  });

  return (
    <ButtonGroupProvider value={context}>
      <Component ref={domRef} className={classNames} {...getButtonGroupProps()}>
        {children}
      </Component>
    </ButtonGroupProvider>
  );
});

ButtonGroup.displayName = "HeroUI.ButtonGroup";

export default ButtonGroup;

"use client";

import type { UseButtonProps } from "./use-button";

import { useButton } from "./use-button";

import { forwardRef } from "@/lib/system";
import { Ripple } from "../ripple";
import { Spinner } from "../spinner";

export interface ButtonProps extends UseButtonProps {}

const Button = forwardRef<"button", ButtonProps>((props, ref) => {
  const {
    Component,
    domRef,
    children,
    spinnerSize,
    spinner = <Spinner color="current" size={spinnerSize} />,
    spinnerPlacement,
    startContent,
    endContent,
    isLoading,
    disableRipple,
    getButtonProps,
    getRippleProps,
    isIconOnly,
  } = useButton({ ...props, ref });

  return (
    <Component ref={domRef} {...getButtonProps()}>
      {startContent}
      {isLoading && spinnerPlacement === "start" && spinner}
      {isLoading && isIconOnly ? null : children}
      {isLoading && spinnerPlacement === "end" && spinner}
      {endContent}
      {!disableRipple && <Ripple {...getRippleProps()} />}
    </Component>
  );
});

Button.displayName = "HeroUI.Button";

export default Button;

