/**
 * Returns an array of numbers, starting at `start` and ending at `end`.
 * @param start number
 * @param end number
 * @returns number[]
 */
export function range(start: number, end: number) {
  const length = end - start + 1;

  return Array.from({ length }, (_, index) => index + start);
}

/**
 * Clamps a value between a minimum and maximum range.
 *
 * @param value - The value to be clamped.
 * @param min - The minimum value of the range.
 * @param max - The maximum value of the range.
 * @returns The clamped value.
 */
export function clamp(value: number, min: number, max: number) {
  return Math.min(Math.max(value, min), max);
}

/**
 * Clamps a value between 0 and a maximum value.
 * @param value - The value to clamp.
 * @param max - The maximum value to clamp to. Defaults to 100.
 * @returns The clamped value.
 */
export function clampPercentage(value: number, max: number = 100) {
  return Math.min(Math.max(value, 0), max);
}

import { isObject } from "./assertion";

type Key = string | number;

export interface MergeObject {
  [key: string]: any;
}

export const renameProp = (oldProp: string, newProp: string, { [oldProp]: old, ...others }) => ({
  [newProp]: old,
  ...others,
});

// copy an object without reference
export const copyObject = (obj: any) => {
  if (!isObject(obj)) return obj;
  if (obj instanceof Array) return [...obj];

  return { ...obj };
};

// copy an object omit some keys
export const omitObject = (obj: any, omitKeys: string[]) => {
  if (!isObject(obj)) return obj;
  if (obj instanceof Array) return [...obj];
  const newObj = { ...obj };

  omitKeys.forEach((key) => newObj[key] && delete newObj[key]);

  return newObj;
};

// clean undefined and null values
export const cleanObject = (obj: any) => {
  if (!isObject(obj)) return obj;
  if (obj instanceof Array) return [...obj];
  const newObj = { ...obj };

  Object.keys(newObj).forEach((key) => {
    if (newObj[key] === undefined || newObj[key] === null) {
      delete newObj[key];
    }
  });

  return newObj;
};

export const cleanObjectKeys = (obj: any, keys: string[] = []) => {
  if (!isObject(obj)) return obj;
  if (obj instanceof Array) return [...obj];
  const newObj = { ...obj };

  keys.forEach((key) => {
    if (newObj[key]) {
      delete newObj[key];
    }
  });

  return newObj;
};

export const getKeyValue = (obj: any, key: Key) => {
  if (!isObject(obj)) return obj;
  if (obj instanceof Array) return [...obj];

  return obj[key];
};

/**
 * Get value from a deeply nested object using a string path.
 * Memorizes the value.
 * @param obj - the object
 * @param path - the string path
 * @param fallback  - the fallback value
 */
export const getProp = (obj: Record<string, any>, path: string | number, fallback?: any, index?: number) => {
  const key = typeof path === "string" ? path.split(".") : [path];

  for (index = 0; index < key.length; index += 1) {
    if (!obj) break;
    obj = obj[key[index]];
  }

  return obj === undefined ? fallback : obj;
};

/**
 * Converting an array of objects into a single object.
 * @param arr - the array of objects
 * @returns the single object
 */
export const arrayToObject = (arr: any[]) => {
  if (!arr.length || !Array.isArray(arr)) return {};

  return arr.reduce((acc, item) => {
    return { ...acc, ...item };
  }, {});
};

export function compact<T extends Record<any, any>>(object: T) {
  const clone = Object.assign({}, object);

  for (let key in clone) {
    if (clone[key] === undefined) delete clone[key];
  }

  return clone;
}

import type { MutableRefObject, Ref } from "react";

import { clsx } from "./clsx";

// Partial code from react-spectrum to avoid importing the entire package
interface Props {
  [key: string]: any;
}

type PropsArg = Props | null | undefined;

// taken from: https://stackoverflow.com/questions/51603250/typescript-3-parameter-list-intersection-type/51604379#51604379
type TupleTypes<T> = { [P in keyof T]: T[P] } extends { [key: number]: infer V } ? NullToObject<V> : never;
type NullToObject<T> = T extends null | undefined ? {} : T;

type UnionToIntersection<U> = (U extends any ? (k: U) => void : never) extends (k: infer I) => void ? I : never;

/**
 * Calls all functions in the order they were chained with the same arguments.
 */
export function chain(...callbacks: any[]): (...args: any[]) => void {
  return (...args: any[]) => {
    for (let callback of callbacks) {
      if (typeof callback === "function") {
        callback(...args);
      }
    }
  };
}

export let idsUpdaterMap: Map<string, { current: string | null }[]> = new Map();
/**
 * Merges two ids.
 * Different ids will trigger a side-effect and re-render components hooked up with `useId`.
 */
export function mergeIds(idA: string, idB: string): string {
  if (idA === idB) {
    return idA;
  }

  let setIdsA = idsUpdaterMap.get(idA);

  if (setIdsA) {
    setIdsA.forEach((ref) => (ref.current = idB));

    return idB;
  }

  let setIdsB = idsUpdaterMap.get(idB);

  if (setIdsB) {
    setIdsB.forEach((ref) => (ref.current = idA));

    return idA;
  }

  return idB;
}

/**
 * Merges multiple props objects together. Event handlers are chained,
 * classNames are combined, and ids are deduplicated - different ids
 * will trigger a side-effect and re-render components hooked up with `useId`.
 * For all other props, the last prop object overrides all previous ones.
 * @param args - Multiple sets of props to merge together.
 */

export function mergeProps<T extends PropsArg[]>(...args: T): UnionToIntersection<TupleTypes<T>> {
  // Start with a base clone of the first argument. This is a lot faster than starting
  // with an empty object and adding properties as we go.
  let result: Props = { ...args[0] };

  for (let i = 1; i < args.length; i++) {
    let props = args[i];

    for (let key in props) {
      let a = result[key];
      let b = props[key];

      // Chain events
      if (
        typeof a === "function" &&
        typeof b === "function" &&
        // This is a lot faster than a regex.
        key[0] === "o" &&
        key[1] === "n" &&
        key.charCodeAt(2) >= /* 'A' */ 65 &&
        key.charCodeAt(2) <= /* 'Z' */ 90
      ) {
        result[key] = chain(a, b);

        // Merge classnames, sometimes classNames are empty string which eval to false, so we just need to do a type check
      } else if (
        (key === "className" || key === "UNSAFE_className") &&
        typeof a === "string" &&
        typeof b === "string"
      ) {
        result[key] = clsx(a, b);
      } else if (key === "id" && a && b) {
        result.id = mergeIds(a, b);
        // Override others
      } else {
        result[key] = b !== undefined ? b : a;
      }
    }
  }

  return result as UnionToIntersection<TupleTypes<T>>;
}

export function mergeRefs<T>(...refs: Array<Ref<T> | MutableRefObject<T> | null | undefined>): Ref<T> {
  if (refs.length === 1 && refs[0]) {
    return refs[0];
  }

  return (value: T | null) => {
    let hasCleanup = false;

    const cleanups = refs.map((ref) => {
      const cleanup = setRef(ref, value);

      hasCleanup ||= typeof cleanup == "function";

      return cleanup;
    });

    if (hasCleanup) {
      return () => {
        cleanups.forEach((cleanup, i) => {
          if (typeof cleanup === "function") {
            cleanup?.();
          } else {
            setRef(refs[i], null);
          }
        });
      };
    }
  };
}

function setRef<T>(ref: Ref<T> | MutableRefObject<T> | null | undefined, value: T): (() => void) | void {
  if (typeof ref === "function") {
    return () => ref(value);
  } else if (ref != null) {
    if ("current" in ref) {
      (ref as MutableRefObject<T>).current = value;
    }
  }
}

export const isPatternNumeric = (pattern: string) => {
  const numericPattern = /(^|\W)[0-9](\W|$)/;

  return numericPattern.test(pattern) && !/[^\d\^$\[\]\(\)\*\+\-\.\|]/.test(pattern);
};

export const safeText = (text: string): string => {
  if (text?.length <= 4) return text;

  return text?.slice(0, 3);
};

export const safeInitials = (text: string): string => {
  const initials =
    text
      ?.trim()
      .split(/[\s\-_.]+/)
      .filter(Boolean)
      .map((word) => word.charAt(0).toUpperCase())
      .join("") || "";

  return safeText(initials);
};

export const safeAriaLabel = (...texts: any[]): string => {
  let ariaLabel = " ";

  // loop through all texts and return the first non-empty string
  for (const text of texts) {
    if (typeof text === "string" && text.length > 0) {
      ariaLabel = text;
      break;
    }
  }

  return ariaLabel;
};

